import React from "react";
import "./styles/MainApp.css";
import NavBar from "../components/NavBar";
import EmployeeForm from "../components/EmployeeForm";
import EmployeeTable from "../components/EmployeeTable";
import { v4 as uuid } from "uuid";
import formValidation from "../components/formValidation";

const formInitialState = {
  form: {
    name: "",
    nameError: "",
    email: "",
    emailError: "",
    role: "",
    roleError: "",
    dob: "",
    dobError: "",
    gender: "",
    genderError: "",
  },
};
class mainApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: true,
      error: null,
      form: {
        name: "",
        nameError: null,
        email: "",
        emailError: null,
        role: "",
        roleError: null,
        dob: "",
        dobError: null,
        gender: "",
        genderError: null,
      },
      table: {
        submit: false,
        data: [
          // {
          //   name: "Raynulfo Mata",
          //   email: "rjmata95@gmail.com",
          //   role: "JrDev",
          //   dob: "12/19/1995",
          //   gender: "male",
          // },
        ],
      },
    };
  }

  componentDidMount() {
    // this.setState({
    //   loading: false,
    // });
    this.fetchData();
  }

  fetchData = async () => {
    this.setState({ loading: true });
    try {
      const response = await fetch("http://localhost:8080/table");
      const data = await response.json();
      this.setState({
        loading: false,
        table: { data: data.body },
      });

      console.log(data);
    } catch (error) {
      this.setState({
        loading: false,
        error,
      });
      console.log(`there's been an error: ${error}`);
    }
  };

  setNewEntries = () => {};

  formChange = (e) => {
    this.setState((state) => ({
      form: {
        ...state.form,
        [e.target.name]: e.target.value,
      },
    }));
  };
  formSubmitted = (e) => {
    e.preventDefault();
    //validate entries from this.state.form
    this.setState((state) => {
      const newData = state.table.data.concat({
        _id: uuid(),
        ...state.form,
      });
      return { table: { data: newData } };
    });

    this.setState(formInitialState);
    //toggle submit to true (may not be necessary)
  };
  render() {
    return (
      <div className="green-bg">
        <NavBar />
        <div>
          <EmployeeForm
            onChange={this.formChange}
            formState={this.state.form}
            onSubmit={this.formSubmitted}
          />
        </div>
        <div>
          <EmployeeTable updateTable={this.state.table.data} />
        </div>
      </div>
    );
  }
}

export default mainApp;
