import React from "react";
import "./styles/EmployeeTable.css";
import {
  FaTrashAlt,
  FaEdit,
  FaCheckCircle,
  FaWindowClose,
} from "react-icons/fa";
import { BeatLoader } from "react-spinners";

const spinner = {
  display: "flex",
  height: "100%",
  alignItems: "center",
  justifyContent: "center",
};

class EmployeeTable extends React.Component {
  state = {
    editing: false,
    editingID: "",
  };

  componentDidMount() {
    console.log("tBLE");
  }

  onClick(id) {
    console.log(id);
  }

  editing = (id) => {
    this.setState({ editing: true, editingID: id });
  };

  render() {
    return (
      <div className="container__component">
        <div className="container__table">
          {this.props.isLoading ? (
            <BeatLoader loading color={"#9090ff"} css={spinner} />
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>E-mail</th>
                  <th>Role</th>
                  <th>DOB</th>
                  <th>Gender</th>
                </tr>
              </thead>
              <tbody>
                {this.props.updateTable.map((element) => {
                  if (this.state.editing) {
                    if (element._id === this.state.editingID) {
                      return (
                        <tr key={element._id}>
                          <td>
                            <input value={element.name} />
                          </td>
                          <td>
                            <input value={element.email} />
                          </td>
                          <td>
                            <select value={element.role}>
                              <option value="">Select Your Role</option>
                              <option value="FDJr">
                                Front-end Developer Jr
                              </option>
                              <option value="FDSr">
                                Front-end Developer Sr
                              </option>
                              <option value="BDJr">
                                Back-end Developer Jr
                              </option>
                              <option value="BDSr">
                                Back-end Developer Sr
                              </option>
                              <option value="PM">Product Manager</option>
                              <option value="RA">Risks Analyst</option>
                              <option value="UID">UI Designer</option>
                              <option value="UXD">UX Designer</option>
                            </select>
                          </td>
                          <td>
                            <input type="date" value={element.dob} />
                          </td>
                          <td>
                            <select value={element.gender}>
                              <option value="male">Male</option>
                              <option value="female">Female</option>
                              <option value="other">Other</option>
                            </select>
                          </td>
                          <td>
                            <FaCheckCircle
                              onClick={() =>
                                this.setState({ editing: false, editingID: "" })
                              }
                              className={"actionBtn"}
                            />
                          </td>
                          <td>
                            <FaWindowClose
                              onClick={() =>
                                this.setState({ editing: false, editingID: "" })
                              }
                              className={"actionBtn"}
                            />
                          </td>
                        </tr>
                      );
                    }
                  }

                  return (
                    <tr key={element._id}>
                      <td>{element.name}</td>
                      <td>{element.email}</td>
                      <td>{element.role}</td>
                      <td>{element.dob}</td>
                      <td>{element.gender}</td>
                      <td>
                        <FaTrashAlt
                          onClick={() => this.props.deleteHandler(element._id)}
                          className={"actionBtn"}
                        />
                      </td>
                      <td>
                        <FaEdit
                          onClick={() => this.editing(element._id)}
                          className={"actionBtn"}
                        />
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      </div>
    );
  }
}

export default EmployeeTable;
