import React from "react";
import "./styles/EmployeeForm.css";
// import "../../node_modules/bootstrap/dist/css/bootstrap.css";

class EmployeeForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.printState = this.printState.bind(this);
    this.changeHandler = this.changeHandler.bind(this);
  }
  changeHandler(e) {
    this.setState({
      [e.target.name]: e.target.value,
    });
    console.log(this.state);
  }
  printState() {
    console.log(this.state);
  }

  resetState = (e) => {
    this.setState((state) => ({}));
  };
  submitHandler = (e) => {
    e.preventDefault();
    this.setState({
      [e.target.name]: e.target.value,
    });
    console.log(this.state);
  };
  // changeHandler = (e) => {
  //   console.log(e);
  // };
  render() {
    return (
      <form onSubmit={this.submitHandler}>
        <div className="row ">
          <label>Name:</label>
          <input
            onChange={this.changeHandler}
            type="text"
            name="name"
            value={this.state.name}
          />
        </div>
        <div className="row ">
          <label>E-mail:</label>
          <input
            onChange={this.changeHandler}
            type="email"
            name="email"
            value={this.state.email}
          />
        </div>
        <div className="row ">
          <label>Role:</label>
          <select
            onChange={this.changeHandler}
            name="role"
            value={this.state.role}
          >
            <option value="none">Select Your Role</option>
            <option value="FDJr">Front-end Developer Jr</option>
            <option value="FDSr">Front-end Developer Sr</option>
            <option value="BDJr">Back-end Developer Jr</option>
            <option value="BDSr">Back-end Developer Sr</option>
            <option value="PM">Product Manager</option>
            <option value="RA">Risks Analyst</option>
            <option value="UID">UI Designer</option>
            <option value="UXD">UX Designer</option>
          </select>
        </div>
        <div className="row ">
          <label>DOB:</label>
          <input
            onChange={this.changeHandler}
            type="date"
            name="dob"
            value={this.state.dob}
          />
        </div>
        <div>
          <label className="row ">
            Gender:
            <span>Male</span>
            <input type="radio" name="gender" value="male" />
            <span>Female</span>
            <input type="radio" name="gender" value="female" />
            <span>Other</span>
            <input type="radio" name="gender" value="other" />
          </label>
        </div>
        <div className="row centered">
          <button>Submit</button>
          <button
            type="button"
            style={{ "margin-left": "2em" }}
            onClick={this.printState}
          >
            Print State
          </button>
          <button
            type="button"
            style={{ "margin-left": "2em" }}
            onClick={this.resetState}
          >
            reset State
          </button>
        </div>
      </form>
    );
  }
}

export default EmployeeForm;
