import React from "react";
import "./styles/EmployeeTable.css";
import { FaTrashAlt, FaEdit, FaCircleNotch } from "react-icons/fa";
import { BounceLoader } from "react-spinners";

const spinner = css`
  display: block;
  margin: 0 auto;
  border-color: red;
`;

class EmployeeTable extends React.Component {
  componentDidMount() {
    console.log("tBLE");
  }

  onClick(id) {
    console.log(id);
  }

  render() {
    return (
      <div className="container__component">
        <div className="container__table">
          {this.props.isLoading ? (
            <BounceLoader loading color={"#9090ff"} css={spinner} />
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>E-mail</th>
                  <th>Role</th>
                  <th>DOB</th>
                  <th>Gender</th>
                </tr>
              </thead>
              <tbody>
                {this.props.updateTable.map((element) => {
                  return (
                    <tr key={element._id}>
                      <td>{element.name}</td>
                      <td>{element.email}</td>
                      <td>{element.role}</td>
                      <td>{element.dob}</td>
                      <td>{element.gender}</td>
                      <td>
                        <FaTrashAlt
                          onClick={() => this.props.deleteHandler(element._id)}
                        />
                      </td>
                      <td>
                        <FaEdit
                          onClick={() => this.props.deleteHandler(element._id)}
                        />
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          )}
        </div>
      </div>
    );
  }
}

export default EmployeeTable;
